from fastapi import FastAPI
import uvicorn
# Standard library imports should be done before external libraries
from random import randint


app = FastAPI()


# -1: The function should be called "home_route"
@app.get("/")
def home_root():
    # -1: The response should be a string, not a set
    return {"Unit 3 Quiz"}


@app.get("/about_me")
def about_me():
    return {"name": "Adriaan"}

@app.get("/helper/random_number")
def random_number_helper():
    random_num = randint(0, )
    return {"number": random_num}

if __name__ == "__main__":
    uvicorn.run("main:app", reload=True)
